#!/bin/bash

yum install -y shadow-utils sudo openssh-server
yum clean all

ssh-keygen -A

echo '%wheel ALL=(ALL) NOPASSWD: ALL' > /etc/sudoers.d/wheel-nopasswd

useradd -G wheel vagrant
echo 'vagrant:vagrant' | chpasswd
echo 'root:vagrant' | chpasswd

mkdir -p /root/.ssh /home/vagrant/.ssh
chmod 0700 /root/.ssh /home/vagrant/.ssh

echo ssh-rsa  vagrant insecure public key > /root/.ssh/authorized_keys
echo ssh-rsa  vagrant insecure public key > /home/vagrant/.ssh/authorized_keys
chmod 0700 /root/.ssh /home/vagrant/.ssh
chown -R vagrant: /home/vagrant/.ssh
