#!/bin/bash

yum -y swap -- remove fakesystemd -- install systemd systemd-libs
yum install -y shadow-utils sudo openssh-server openssh-clients
yum clean all

# Setup SystemD
# Taken from github.com/dockerimages/docker-systemd
(cd /lib/systemd/system/sysinit.target.wants/; for i in *; do [ $i == systemd-tmpfiles-setup.service ] || rm -f $i; done); \
rm -f /etc/systemd/system/*.wants/*;\
rm -f /lib/systemd/system/local-fs.target.wants/*; \
rm -f /lib/systemd/system/sockets.target.wants/*udev*; \
rm -f /lib/systemd/system/sockets.target.wants/*initctl*; \
rm -f /lib/systemd/system/basic.target.wants/*;\
rm -f /lib/systemd/system/anaconda.target.wants/*;

systemctl set-default multi-user.target

# Setup SSH

ssh-keygen -A
systemctl enable sshd.service

# Setup users

echo '%wheel ALL=(ALL) NOPASSWD: ALL' > /etc/sudoers.d/wheel-nopasswd

useradd -G wheel vagrant
echo 'vagrant:vagrant' | chpasswd
echo 'root:vagrant' | chpasswd

mkdir -p /root/.ssh /home/vagrant/.ssh
chmod 0700 /root/.ssh /home/vagrant/.ssh

echo ssh-rsa  vagrant insecure public key > /root/.ssh/authorized_keys
echo ssh-rsa  vagrant insecure public key > /home/vagrant/.ssh/authorized_keys
chmod 0700 /root/.ssh /home/vagrant/.ssh
chown -R vagrant: /home/vagrant/.ssh
